mapper.select:
SELECT BD_NO ,BE_NO ,DEP_NO ,BD_DATE ,QUANTITY ,AMMOUNT ,CON_VER ,NOTE FROM BD
AND BD_NO = #{bdNo}
AND BE_NO = #{model}
AND DEP_NO = #{depNo}
AND BD_DATE = #{bdDate}
AND QUANTITY = #{quantity}
AND AMMOUNT = #{ammount}
AND UPPER( CON_VER ) LIKE UPPER ( '%' || #{conVer} || '%' )
AND BD_DATE BETWEEN #{first} AND #{last} + 1
AND USEABLE = 1
ORDER BY BD_NO DESC
ORDER BY BE_NO DESC
ORDER BY DEP_NO DESC
ORDER BY BD_DATE DESC
ORDER BY QUANTITY DESC
ORDER BY AMMOUNT DESC
