mapper.select:
SELECT BD_NO ,BE_NO ,DEP_NO ,BD_DATE ,QUANTITY ,AMMOUNT ,USEABLE ,CON_VER ,NOTE FROM BD
AND BD_NO = #{bdNo}
AND BE_NO = #{beNo}
AND DEP_NO = #{depNo}
AND BD_DATE = #{bdDate}
AND BD_DATE BETWEEN #{first} AND #{last} + 1
AND USEABLE = #{useable}
ORDER BY BD_NO ASC
ORDER BY BE_NO ASC
ORDER BY DEP_NO ASC
ORDER BY BD_DATE DESC
ORDER BY QUANTITY DESC
ORDER BY AMMOUNT DESC
